import pygame
from pygame.locals import *

area_map_width = 10000
area_map_height = 10000
area_map = pygame.Surface((area_map_width,area_map_height))


class Block(pygame.sprite.Sprite):    # a solid block with a collision rect to constrain the player
    def __init__(self, location, colour, size):
        super().__init__()
        self.image = pygame.Surface((size, size))
        self.image.fill(colour)
        self.rect = self.image.get_rect(topleft = location)

class Empty(pygame.sprite.Sprite):   # blocks to be the floor that do not get colision checked
    def __init__(self, location, colour, size):
        super().__init__()
        self.image = pygame.Surface((size, size))
        self.image.fill(colour)
        self.rect = self.image.get_rect(topleft = location)

